import React, {useState, useEffect} from 'react'
import useAuth from './authSpotify'
import Player from './player'
import TrackSearchResult from './TrackSearchResult'
import {Container, Form} from 'react-bootstrap'
import SpotifyWebApi from 'spotify-web-api-node'
import Navbar from '../main/NavBar'
import style from '../../containers/podcasts/podcasts.module.scss'

const spotifyApi = new SpotifyWebApi({
  clientId: '53f114ac392c452c81eaa033513dd209',
})

// eslint-disable-next-line react/prop-types
export default function Dashboard({code}) {
  const accessToken = useAuth(code)
  const [search, setSearch] = useState('')
  const [searchResults, setSearchResults] = useState([])
  const [playingTrack, setPlayingTrack] = useState()

  function chooseTrack(track) {
    setPlayingTrack(track)
    setSearch('')
  }

  useEffect(() => {
    if (!accessToken) return
    spotifyApi.setAccessToken(accessToken)
  }, [accessToken])

  useEffect(() => {
    if (!search) return setSearchResults([])
    if (!accessToken) return

    let cancel = false
    spotifyApi
      .search(search, [
        'album',
        'artist',
        'playlist',
        'track',
        'show',
        'episode',
      ])
      .then((res) => {
        console.log(res.body.tracks, res.body.shows, res.body.episodes)
        if (cancel) return
        setSearchResults(
          res.body.episodes.items.map((show) => {
            const smallestAlbumImage = show.images.reduce((smallest, image) => {
              if (image.height < smallest.height) return image
              return smallest
            }, show.images[0])

            return {
              title: show.name,
              uri: show.uri,
              albumUrl: smallestAlbumImage.url,
            }
          })
        )
      })

    return () => (cancel = true)
  }, [search, accessToken])

  return (
    <Container className="d-flex flex-column py-2" style={{height: '100vh'}}>
      <Navbar />
      <br />
      <h1 className={style.headline}>Transfers</h1>
      <Form.Control
        type="search"
        placeholder="Search Songs/Artists"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <div
        className="flex-grow-1 my-2"
        style={{overflowY: 'auto', color: 'white'}}
      >
        {searchResults.map((track) => (
          <TrackSearchResult
            track={track}
            key={track.uri}
            chooseTrack={chooseTrack}
          />
        ))}
      </div>
      <div>
        <Player accessToken={accessToken} trackUri={playingTrack?.uri} />
      </div>
    </Container>
  )
}
