import React, {useState} from 'react'
import style from './LoginPage.module.scss'
import logo from '../../images/login_logo.png'
import CopyRight from '../../components/common/CopyRight/CopyRight'
import {getToken} from '../../services/authentication'
import '../../utils/axios'
import {useNavigate} from 'react-router-dom'

const AUTH_URL =
  'https://accounts.spotify.com/authorize?client_id=53f114ac392c452c81eaa033513dd2090&response_type=code&redirect_uri=http://localhost:3000&scope=streaming%20user-read-email%20user-read-private%20user-library-read%20user-library-modify%20user-read-playback-state%20user-modify-playback-state'

const LoginPage = () => {
  const [credentials, setCredentials] = useState({})
  const [isLoginSuccess, setLoginSuccessStatus] = useState(false)
  const navigate = useNavigate()

  const handleChange = (event) => {
    const {name, value} = event.target

    setCredentials((prevValue) => {
      return {
        ...prevValue,
        [name]: value,
      }
    })
  }

